import React from "react";
import "./movie-info-card.css";

function MovieInfoCard({ selectedMovie, onMovieSelect }) {
    const darkSpaceHandleClick = () => {
        onMovieSelect(null);
    }

    const movieCardHandleClick = () => {
        onMovieSelect(null);
    }

    // selectedMovie가 없다면 아무것도 렌더링하지 않음
    if (!selectedMovie) {
        return null;
    }

    // selectedMovie가 있다면 dark-background-space와 함께 렌더링
    return (
        <div id="dark-background-space" onClick={darkSpaceHandleClick}>
            <section className="movie-info-card" onClick={movieCardHandleClick}>
                { selectedMovie }
            </section>
        </div>
    );
}

export default MovieInfoCard;
