import React, { useState, useEffect, useRef } from "react";
import "./movie-list.css";
import MovieItem from "./MovieItem.jsx";

function MovieList({ items, rankDisplayScope, onMovieSelect }) {
    const [isRightButtonVisible, setIsRightButtonVisible] = useState(true);
    const movieListRef = useRef(null);

    const checkScrollPosition = () => {
        const movieList = movieListRef.current;
        if (!movieList) return;

        const isAtStart = movieList.scrollLeft === 0;
        const isAtEnd = movieList.scrollLeft + movieList.offsetWidth >= movieList.scrollWidth;

        setIsRightButtonVisible(!isAtStart && !isAtEnd);
    };

    useEffect(() => {
        const movieList = movieListRef.current;
        if (movieList) {
            movieList.addEventListener('scroll', checkScrollPosition);
            checkScrollPosition(); // 초기 위치 확인
        }

        return () => {
            if (movieList) {
                movieList.removeEventListener('scroll', checkScrollPosition);
            }
        };
    }, []);

    return (
        <div className="movie-list-container">
            <button id="left" className="scroll-button"><img src="/left-arrow.png" alt="" /></button>
            <section ref={movieListRef} className="movie-list" onScroll={checkScrollPosition}>
                {items.map((item, key) => (
                    <MovieItem
                        key={key}
                        index={key}
                        item={item}
                        isRankDisplay={(key < rankDisplayScope) || (rankDisplayScope === undefined)}
                        onMovieSelect={onMovieSelect}
                    />
                ))}
            </section>
            {isRightButtonVisible && <button id="right" className="scroll-button"><img src="/right-arrow.png" alt="" /></button>}
        </div>
    );
}

export default MovieList;
